USE pad;

INSERT INTO `pad`.`email_templates` (`id`, `type`, `name`, `account_id`, `config_id`, `user`, `email_from`, `email_from_password`, `email_from_alias`, `email_bcc`, `subject`, `template`, `message`, `variables`, `priority`, `operator_id`, `date_created`, `date_edited`) VALUES ('1', '-1', 'Password Reset', '-1', '1', '', 'no-reply@allpointspayments.com', 'gaf4mAYuC8ch', 'PAD - TEST', '', 'PAD - Reset Your Password', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n   <head>\n      <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n      <title>SCF - </title>\n      <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"/>\n   </head>\n   <body style=\"margin: 0; padding: 0;\">\n      <div style=\"margin:0;background-color:#f3f3f4\">\n         <div style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\">\n            <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\">\n               <tbody>\n                  <tr>\n                     <td align=\"center\">\n                        <table width=\"600px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#f3f3f4\" align=\"center\" style=\"border-spacing:0\">\n                           <tbody>\n                              <tr>\n                                 <td width=\"600\" bgcolor=\"#f3f3f4\" style=\"border-collapse:collapse\">\n                                    <table width=\"600\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" bgcolor=\"#f3f3f4\" style=\"border-spacing:0;margin-bottom:0;margin-left:auto;margin-right:auto;margin-top:40px\">\n                                       <tbody>\n                                          <tr>\n                                             <td width=\"600\" height=\"100\" bgcolor=\"#337AB7\" style=\"border-collapse:collapse;background:#337AB7;border-radius:3px 3px 0 0\">\n                                                <table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border-spacing:0;margin:0 auto\">\n                                                   <tbody>\n                                                      <tr>\n                                                         <td>\n                                                            <h4 style=\"color: white;font-size: 30px;\">Port Access Dakar</h4>\n                                                         </td>\n                                                      </tr>\n                                                   </tbody>\n                                                </table>\n                                             </td>\n                                          </tr>\n                                          <tr>\n                                             <td width=\"600\" bgcolor=\"#ffffff\">\n                                                ${templateBody}\n                                             </td>\n                                          </tr>\n                                          <tr>\n                                             <td style=\"background-color:#ffffff;border-radius:0 0 3px 3px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9;border-bottom:1px solid #e8e8e9\">\n                                                &nbsp;\n                                             </td>\n                                          </tr>\n                                          <tr>\n                                             <td width=\"600\" height=\"20\" style=\"border-collapse:collapse\">&nbsp;</td>\n                                          </tr>\n                                          <tr style=\"font-size:12px;color:#999999\">\n                                             <td width=\"600\" align=\"left\" style=\"border-collapse:collapse\">\n                                                <p style=\"line-height:21px;margin-bottom:15px;margin-left:0;margin-right:0;margin-top:28px\">CONFIDENTIAL! This email contains confidential information and is intended for the authorised recipient only. You may neither use nor edit the email including attachments, nor make them accessible to third parties in any manner whatsoever. Any unauthorised copying, disclosure or distribution of the material in this email is strictly forbidden. Thank you for your co-operation. If you received this message by mistake, please delete this email.\n                                                   <br><br>\n                                                   SAVE PAPER - Please do not print this e-mail unless absolutely necessary\n                                                </p>\n                                             </td>\n                                          </tr>\n                                          <tr>\n                                             <td width=\"600\" height=\"50\" style=\"border-collapse:collapse\">&nbsp;</td>\n                                          </tr>\n                                       </tbody>\n                                    </table>\n                                 </td>\n                              </tr>\n                           </tbody>\n                        </table>\n                     </td>\n                  </tr>\n               </tbody>\n            </table>\n         </div>\n      </div>\n   </body>\n</html>', '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\">\n   <tbody>\n      <tr>\n         <td>\n            <p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${firstName},</p>\n         </td>\n      </tr>\n      <tr>\n         <td>\n            <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\">\n               <tbody>\n                  <tr>\n                     <td>\n                        <p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">You are receiving this email because a request has been submitted to change your password. You can use the link below to set a new password.</p>\n                     </td>\n                  </tr>\n                  <tr>\n                     <td>\n                        <p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\"><a href=\"${resetPasswordLink}\"><strong>Click Here</strong></a> to set a new password.\n                        </p>\n                     </td>\n                  </tr>\n                  <tr>\n                     <td>\n                        <p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Please note that this link is valid only for the\n                           <strong>next 30 minutes</strong>.\n                        </p>\n                     </td>\n                  </tr>\n                  <tr>\n                     <td>\n                        <p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">If you did not ask to reset your password, just\n                           <a href=\"${reportLink}\">let us know</a>.\n                        </p>\n                     </td>\n                  </tr>\n                  <tr>\n                     <td>\n                        <p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                           Kind Regards,\n                           <br><strong>PAD Support Team</strong>\n                        </p>\n                     </td>\n                  </tr>\n               </tbody>\n            </table>\n         </td>\n      </tr>\n   </tbody>\n</table>', '', '15', '-1', '2019-01-09 11:52:30', '2019-01-09 11:52:30');

INSERT INTO `email_config` (`id`,`smtp_host`,`smtp_auth`,`smtp_port`,`smtp_socket_factory_port`,`smtp_socket_factory_class`,`smtp_socket_factory_fallback`,`smtp_starttls_enable`,`operator_id`,`date_created`,`date_edited`) VALUES (1,'smtp.gmail.com','true','465','465','javax.net.ssl.SSLSocketFactory','false',0,-1,'2018-03-01 09:28:19','2018-03-01 09:28:19');


CREATE TABLE `system_checks` (
  `id` int(11) NOT NULL,
  `name` varchar(64) NOT NULL,
  `is_active` tinyint(1) NOT NULL,
  `type` int(11) NOT NULL,
  `is_query` tinyint(1) NOT NULL,
  `query_time_from` time NOT NULL,
  `query_time_to` time NOT NULL,
  `query` varchar(512) NOT NULL,
  `query_params` varchar(128) NOT NULL,
  `query_result` int(11) NOT NULL,
  `query_execution_time` int(11) NOT NULL,
  `comparison_operator` int(11) NOT NULL,
  `comparison_value` int(11) NOT NULL,
  `comparison_result` tinyint(1) NOT NULL,
  `date_edited` datetime NOT NULL,
  `date_created` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `system_timer_tasks` (
  `id` int(11) NOT NULL,
  `name` varchar(64) NOT NULL,
  `date_last_run` datetime NOT NULL,
  `type` varchar(16) NOT NULL,
  `period` varchar(32) NOT NULL,
  `application` varchar(32) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `unauthorised_activity_reports` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `contact_id` int(11) NOT NULL,
  `email_template_id` int(11) NOT NULL,
  `date_created` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

