USE pad;

UPDATE `pad`.`email_templates` SET `template`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\"><html xmlns=\"http://www.w3.org/1999/xhtml\"><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" /><title>SCF - </title><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"/></head><body style=\"margin: 0; padding: 0;\"><div style=\"margin:0;background-color:#f3f3f4\"><div style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><tbody><tr><td align=\"center\"><table width=\"600px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#f3f3f4\" align=\"center\" style=\"border-spacing:0\"><tbody><tr><td width=\"600\" bgcolor=\"#f3f3f4\" style=\"border-collapse:collapse\"><table width=\"600\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" bgcolor=\"#f3f3f4\" style=\"border-spacing:0;margin-bottom:0;margin-left:auto;margin-right:auto;margin-top:40px\"><tbody><tr><td width=\"600\" height=\"100\" bgcolor=\"#337AB7\" style=\"border-collapse:collapse;background:#337AB7;border-radius:3px 3px 0 0\"><table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border-spacing:0;margin:0 auto\"><tbody><tr><td><h4 style=\"color: white;font-size: 30px;\">Port Access Dakar</h4></td></tr></tbody></table></td></tr><tr><td width=\"600\" bgcolor=\"#ffffff\">\n                                                ${templateBody}\n                                             </td></tr><tr><td style=\"background-color:#ffffff;border-radius:0 0 3px 3px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9;border-bottom:1px solid #e8e8e9\">\n                                                &nbsp;\n                                             </td></tr><tr><td width=\"600\" height=\"20\" style=\"border-collapse:collapse\">&nbsp;</td></tr><tr style=\"font-size:12px;color:#999999\"><td width=\"600\" align=\"left\" style=\"border-collapse:collapse\"><p style=\"line-height:21px;margin-bottom:15px;margin-left:0;margin-right:0;margin-top:28px\">CONFIDENTIAL! This email contains confidential information and is intended for the authorised recipient only. You may neither use nor edit the email including attachments, nor make them accessible to third parties in any manner whatsoever. Any unauthorised copying, disclosure or distribution of the material in this email is strictly forbidden. Thank you for your co-operation. If you received this message by mistake, please delete this email.\n                                                   <br><br>\n                                                   SAVE PAPER - Please do not print this e-mail unless absolutely necessary\n                                                </p></td></tr><tr><td width=\"600\" height=\"50\" style=\"border-collapse:collapse\">&nbsp;</td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></div></div></body></html>', `message`='<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${firstName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">You are receiving this email because a request has been submitted to change your password. You can use the link below to set a new password.</p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\"><a href=\"${resetPasswordLink}\"><strong>Click Here</strong></a> to set a new password.\n                        </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Please note that this link is valid only for the\n                           <strong>next 30 minutes</strong>.\n                        </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">If you did not ask to reset your password, just\n                           <a href=\"${reportLink}\">let us know</a>.\n                        </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                           Kind Regards,\n                           <br><strong>PAD Support Team</strong></p></td></tr></tbody></table></td></tr></tbody></table>' WHERE `id`='1';

UPDATE `pad`.`email_templates` SET `template`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\"><html xmlns=\"http://www.w3.org/1999/xhtml\"><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" /><title>PAD - Email Verification Code</title><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"/></head><body style=\"margin: 0; padding: 0;\"><div style=\"margin:0;background-color:#f3f3f4\"><div style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><tbody><tr><td align=\"center\"><table width=\"600px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#f3f3f4\" align=\"center\" style=\"border-spacing:0\"><tbody><tr><td width=\"600\" bgcolor=\"#f3f3f4\" style=\"border-collapse:collapse\"><table width=\"600\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" bgcolor=\"#f3f3f4\" style=\"border-spacing:0;margin-bottom:0;margin-left:auto;margin-right:auto;margin-top:40px\"><tbody><tr><td width=\"600\" height=\"100\" bgcolor=\"#337AB7\" style=\"border-collapse:collapse;background:#337AB7;border-radius:3px 3px 0 0\"><table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border-spacing:0;margin:0 auto\"><tbody><tr><td><h4 style=\"color: white;font-size: 30px;\">Port Access Dakar</h4></td></tr></tbody></table></td></tr><tr><td width=\"600\" bgcolor=\"#ffffff\">\n                                                                ${templateBody}\n                                                                </td></tr><tr><td style=\"background-color:#ffffff;border-radius:0 0 3px 3px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9;border-bottom:1px solid #e8e8e9\">\n                      &nbsp;\n                    </td></tr><tr><td width=\"600\" height=\"20\" style=\"border-collapse:collapse\">&nbsp;</td></tr><tr style=\"font-size:12px;color:#999999\"><td width=\"600\" align=\"left\" style=\"border-collapse:collapse\"><p style=\"line-height:21px;margin-bottom:15px;margin-left:0;margin-right:0;margin-top:28px\">CONFIDENTIAL! This email contains confidential information and is intended for the authorised recipient only. You may neither use nor edit the email including attachments, nor make them accessible to third parties in any manner whatsoever. Any unauthorised copying, disclosure or distribution of the material in this email is strictly forbidden. Thank you for your co-operation. If you received this message by mistake, please delete this email.\n                                            \n                                            \n                                    \n                                    \n                                                                        <br><br>\n                                               SAVE PAPER - Please do not print this e-mail unless absolutely necessary\n                                                 \n                                              \n                                              \n                                      \n                                      \n                                                                            </p></td></tr><tr><td width=\"600\" height=\"50\" style=\"border-collapse:collapse\">&nbsp;</td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></div></div></body></html>', `message`='<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello,</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Thank you for your interest in joining Port Access Dakar.</p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">To start the registration process, please verify your email address by entering the verification code below on the registration page. This verification code will be active for the \n                                                  \n                                                                                                    \n                                    <strong>next ${emailCodeValidityPeriod} minutes</strong>.\n                                                        \n                                                \n                                                \n                                                                                                \n                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Your email address verification code is \n                                                  \n                                                  \n                                                                                                    \n                                    <strong>${verificationCode}</strong>.\n                                                        \n                                                \n                                                \n                                                                                                \n                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                                    Kind Regards,\n                                                          \n                                                          \n                                                          \n                                                  \n                                                  \n                                                                                                    \n                                    <br><strong>PAD Support Team</strong></p></td></tr></tbody></table></td></tr></tbody></table>' WHERE `id`='2';

UPDATE `pad`.`email_templates` SET `template`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\"><html xmlns=\"http://www.w3.org/1999/xhtml\"><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" /><title>PAD -  Registration Successful </title><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"/></head><body style=\"margin: 0; padding: 0;\"><div style=\"margin:0;background-color:#f3f3f4\"><div style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background-color:#f3f3f4;font-size:14px;font-family:\'Helvetica Neue\',Helvetica,Arial,sans-serif;color:#333333;padding-bottom:0;padding-left:0;padding-right:0;padding-top:0;margin-bottom:0;margin-left:0;margin-right:0;margin-top:0\"><tbody><tr><td align=\"center\"><table width=\"600px\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#f3f3f4\" align=\"center\" style=\"border-spacing:0\"><tbody><tr><td width=\"600\" bgcolor=\"#f3f3f4\" style=\"border-collapse:collapse\"><table width=\"600\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" bgcolor=\"#f3f3f4\" style=\"border-spacing:0;margin-bottom:0;margin-left:auto;margin-right:auto;margin-top:40px\"><tbody><tr><td width=\"600\" height=\"100\" bgcolor=\"#337AB7\" style=\"border-collapse:collapse;background:#337AB7;border-radius:3px 3px 0 0\"><table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border-spacing:0;margin:0 auto\"><tbody><tr><td><h4 style=\"color: white;font-size: 30px;\">Port Access Dakar</h4></td></tr></tbody></table></td></tr><tr><td width=\"600\" bgcolor=\"#ffffff\">\n                                                                ${templateBody}\n                                                                                </td></tr></tbody></table></td></tr><tr><td style=\"background-color:#ffffff;border-radius:0 0 3px 3px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9;border-bottom:1px solid #e8e8e9\">\n                      &nbsp;\n                    </td></tr><tr><td width=\"600\" height=\"20\" style=\"border-collapse:collapse\">&nbsp;</td></tr><tr style=\"font-size:12px;color:#999999\"><td width=\"600\" align=\"left\" style=\"border-collapse:collapse\"><p style=\"line-height:21px;margin-bottom:15px;margin-left:0;margin-right:0;margin-top:28px\">CONFIDENTIAL! This email contains confidential information and is intended for the authorised recipient only. You may neither use nor edit the email including attachments, nor make them accessible to third parties in any manner whatsoever. Any unauthorised copying, disclosure or distribution of the material in this email is strictly forbidden. Thank you for your co-operation. If you received this message by mistake, please delete this email.\n                                            \n                                            \n                                              \n                                        \n                                    \n                                                                        <br><br>\n                                              SAVE PAPER - Please do not print this e-mail unless absolutely necessary\n                                                  \n                                              \n                                              \n                                                \n                                          \n                                      \n                                                                            </p></td></tr><tr><td width=\"600\" height=\"50\" style=\"border-collapse:collapse\">&nbsp;</td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></div></div></body></html>', `message`='<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${operatorName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">We are delighted to confirm your registration with Port Access Dakar! Please click on the link below to login.\n                                                    \n                                                    </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Please \n                                                        \n                                                            \n                                                      \n                                                  \n                                                                                                    \n                                    <a href=\"${loginPageUrl}\"><strong>click here</strong></a> to login!.\n                                                        \n                                                          \n                                                    \n                                                \n                                                                                                \n                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                                    Kind Regards,\n                                                          \n                                                          \n                                                          \n                                                            \n                                                      \n                                                  \n                                                                                                    \n                                    <br><strong>PAD Support Team</strong></p></td></tr></tbody></table>' WHERE `id`='3';