USE pad;

UPDATE `pad`.`email_templates` SET `message`='<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${firstName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">You are receiving this email because a request has been submitted to change your password. You can use the link below to set a new password.</p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\"><a href=\"${resetPasswordLink}\"><strong>Click Here</strong></a> to set a new password.\n                        \n                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Please note that this link is valid only for the\n                           \n                  <strong>next ${validMinutes} minutes</strong>.\n                        \n                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                           Kind Regards,\n                           \n                  <br><strong>PAD Support Team</strong></p></td></tr></tbody></table></td></tr></tbody></table>' WHERE `id`='1';

UPDATE `pad`.`email_templates` SET `message`='<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Bonjour ${firstName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Vous recevez cet email car une demande de modification de votre mot de passe a été soumise. Vous pouvez utiliser le lien ci-dessous pour définir un nouveau mot de passe.</p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\"><a href=\"${resetPasswordLink}\"><strong>Cliquez ici</strong></a> pour définir un nouveau mot de passe.\n                        \n                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Veuillez noter que ce lien n’est valable que pour le\n                           \n                  <strong>${validMinutes} prochaines minutes</strong>.\n                        \n                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">\n                          Sincères amitiés,\n                           \n                  <br><strong>PAD Support Team</strong></p></td></tr></tbody></table></td></tr></tbody></table>' WHERE `id`='4';
