USE pad;

UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${operatorName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">We are delighted to confirm your registration with AGS’s PAD (Port Access Dakar)! Please note, your account will be activated in the next 24 hours. We will notify you via email when the account is activated. In the meantime, you can add your vehicles and drivers details on the account.</p><br><br>Please click <a href=\"${userGuideUrl}\">here</a> to view the user manual which provides instructions on how to use the system.\n              </td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Kind Regards,\n                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com/pad\n                    </p></td></tr></tbody></table>' WHERE (`id` = '5');
UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Bonjour ${operatorName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Nous sommes ravis de confirmer votre inscription au PAD (Port Access Dakar) d’AGS! Veuillez noter que votre compte sera activé dans les prochaines 24 heures. Nous vous informerons par courrier électronique lorsque le compte est activé. En attendant, vous pouvez ajouter vos véhicules et les détails de vos conducteurs sur le compte.</p><br><br><a href=\"${userGuideUrl}\">Veuillez cliquer ici</a> pour afficher le manuel d\'utilisation qui fournit des instructions sur l\'utilisation du système.\n              </td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Sincères amitiés,\n                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com/pad\n                    </p></td></tr></tbody></table>' WHERE (`id` = '6');
UPDATE `pad`.`sms_templates` SET `message` = 'Hello ${operatorName}. We are delighted to confirm your registration with AGS’s PAD. Your account will be activated in the next 24 hours. Please check the user manual at ${userGuideUrl}' WHERE (`id` = '5');

