USE pad;

UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${operatorName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">We are delighted to confirm your registration with AGS’s PAD (Port Access Dakar)! Please note, your account (${accountNumber}) will be activated in the next 24 hours. We will notify you via email when the account is activated. In the meantime, you can add your vehicles and drivers details on the account.<br><br>Please click <a href=\"${userGuideUrl}\">here</a> to view the user manual which provides instructions on how to use the system.<br><br>Click <a href=\"${loginPageUrl}\">here</a> and use ${loginDetails} to login.\n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Kind Regards,\n                                                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com\n                                                    </p></td></tr></tbody></table></td></tr></tbody></table>' WHERE (`id` = '5');
UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Bonjour ${operatorName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Nous sommes ravis de confirmer votre inscription au PAD (Port Access Dakar) d’AGS! Veuillez noter que votre compte (${accountNumber}) sera activé dans les prochaines 24 heures. Nous vous informerons par courrier électronique lorsque le compte est activé. En attendant, vous pouvez ajouter vos véhicules et les détails de vos conducteurs sur le compte.<br><br><a href=\"${userGuideUrl}\">Veuillez cliquer ici</a> pour afficher le manuel d\'utilisation qui fournit des instructions sur l\'utilisation du système.<br><br><a href=\"${loginPageUrl}\">Veuillez cliquer ici</a> et utilisez ${loginDetails} pour vous connecter.\n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Sincères amitiés,\n                                                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com\n                                                    </p></td></tr></tbody></table></td></tr></tbody></table>' WHERE (`id` = '6');
UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Hello ${accountName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">We are delighted to confirm your account (${accountNumber}) with AGS’s PAD (Port Access Dakar) has been activated!\n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Please \n                                                  <a href=\"${loginPageUrl}\"><strong>click here</strong></a> and use ${loginDetails} to login and book your first trip on PAD. \n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Kind Regards,\n                                                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com\n                                                    </p></td></tr></tbody></table></td></tr></tbody></table>' WHERE (`id` = '7');
UPDATE `pad`.`email_templates` SET `message` = '<table width=\"600\" cellspacing=\"0\" border=\"0\" cellpadding=\"0\" style=\"width:100%;padding:32px 50px 32px;border-left:1px solid #e8e8e9;border-right:1px solid #e8e8e9\"><tbody><tr><td><p style=\"font-family:\'Helvetica Neue\';font-size:26px;font-weight:normal;color:#545457;margin:1em 0\">Bonjour ${accountName},</p></td></tr><tr><td><table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"width:100%\"><tbody><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">Nous sommes ravis de confirmer que votre compte (${accountNumber}) avec le PAD (Port Access Dakar) d’AGS a été activé!\n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#545457;line-height:22px;margin-bottom:1.3em\">S\'il vous plaît <a href=\"${loginPageUrl}\"><strong>Cliquez ici</strong></a> et utilisez ${loginDetails} pour vous connecter et réserver votre premier voyage sur PAD. \n                                                </p></td></tr><tr><td><p style=\"font-family:Helvetica;font-size:16px;color:#707074;line-height:22px;margin-bottom:1em\">Sincères amitiés,\n                                                  <br><strong>PAD Support Team</strong><br>https://www.agsparking.com\n                                                    </p></td></tr></tbody></table></td></tr></tbody></table>' WHERE (`id` = '8');

